'use strict';

var _index = require('./index');

describe('themes utils', function () {
  describe('pxDivide', function () {
    it('should divide by 1 by default', function () {
      expect((0, _index.pxDivide)('2px')).toBe('2px');
    });

    it('should divide the number with the px remaining', function () {
      expect((0, _index.pxDivide)('2px', 2)).toBe('1px');
      expect((0, _index.pxDivide)('12px', 2)).toBe('6px');
    });
  });
});