"use strict";

if (!document.createRange) {
  document.createRange = function () {

    return function () {
      var pub = {};
      pub.setEnd = function (elem) {
        pub.commonAncestorContainer = elem;
      };

      pub.setStart = function (elem) {
        pub.commonAncestorContainer = elem;
      };

      pub.getBoundingClientRect = function () {
        return { right: 0 };
      };

      pub.getClientRects = function () {
        return [];
      };

      return pub;
    }();
  };
}